import{_ as t,c as o,ag as r,o as s}from"./chunks/framework.LfFz_NPV.js";const i="/Crobotic/assets/fond-esp.CK2YTbiv.png",h=JSON.parse('{"title":"⚡ ESP32","description":"","frontmatter":{},"headers":[],"relativePath":"Formations/ESP32/esp32-intro.md","filePath":"Formations/ESP32/esp32-intro.md"}'),a={name:"Formations/ESP32/esp32-intro.md"};function n(l,e,d,u,c,p){return s(),o("div",null,e[0]||(e[0]=[r('<img src="'+i+'" style="height:186px;" alt=""><h1 id="⚡-esp32" tabindex="-1">⚡ ESP32 <a class="header-anchor" href="#⚡-esp32" aria-label="Permalink to &quot;⚡ ESP32&quot;">​</a></h1><h2 id="📖-sommaire-de-la-formation-esp32" tabindex="-1">📖 Sommaire de la Formation ESP32 <a class="header-anchor" href="#📖-sommaire-de-la-formation-esp32" aria-label="Permalink to &quot;📖 Sommaire de la Formation ESP32&quot;">​</a></h2><ol><li><p><a href="./esp32-intro.html"><strong>Introduction à l&#39;ESP32</strong></a></p></li><li><p><a href="./esp32-arduino.html"><strong>Configuration avec Arduino IDE</strong></a></p></li><li><p><a href="./esp32-platformio.html"><strong>Configuration avec Platform IO</strong></a></p></li><li><p><a href="./esp32-wifi.html"><strong>Connexion Wi-Fi avec l&#39;ESP32</strong></a>.</p></li><li><p><a href="./esp32-esphome.html"><strong>Présentation ESPHome</strong></a></p></li><li><p><a href="./esp32-exemple.html"><strong>Exemples pratiques</strong></a></p></li><li><p><a href="./esp32-projet.html"><strong>Projet final : Créez votre projet IoT avec l&#39;ESP32</strong></a></p></li></ol><hr><h2 id="🌟-introduction" tabindex="-1">🌟 Introduction <a class="header-anchor" href="#🌟-introduction" aria-label="Permalink to &quot;🌟 Introduction&quot;">​</a></h2><p>L&#39;ESP32 est une carte de développement <strong>Wi-Fi et Bluetooth</strong> basée sur le module ESP32 d&#39;Espressif. Cette carte est idéale pour les projets IoT grâce à ses fonctionnalités avancées et sa puissance. Voici ses caractéristiques principales :</p><ul><li><strong>Processeur Xtensa dual-core</strong> cadencé à 240 MHz.</li><li><strong>Mémoire</strong> : 520 Ko de SRAM et 4 Mo de flash.</li><li><strong>Connectivité</strong> : Wi-Fi 802.11 b/g/n et Bluetooth 4.2.</li><li><strong>Compatibilité</strong> : Programmable avec l&#39;IDE Arduino, Platform IO ou MicroPython.</li></ul><blockquote><p>📌 <strong>Remarque importante :</strong> L&#39;ESP32 fonctionne en 3.3V, contrairement à des cartes comme l&#39;Arduino Uno qui acceptent aussi le 5V.</p></blockquote><hr><h2 id="🛠️-objectifs-de-la-formation" tabindex="-1">🛠️ Objectifs de la Formation <a class="header-anchor" href="#🛠️-objectifs-de-la-formation" aria-label="Permalink to &quot;🛠️ Objectifs de la Formation&quot;">​</a></h2><ul><li>Comprendre les bases de l&#39;ESP32 et ses fonctionnalités.</li><li>Configurer votre environnement de développement avec Arduino IDE ou Platform IO.</li><li>Apprendre à connecter votre ESP32 à un réseau Wi-Fi.</li><li>Mettre en œuvre des projets pratiques pour maîtriser la programmation et les fonctionnalités IoT.</li></ul><hr><h2 id="🔗-ressources-complementaires" tabindex="-1">🔗 Ressources complémentaires <a class="header-anchor" href="#🔗-ressources-complementaires" aria-label="Permalink to &quot;🔗 Ressources complémentaires&quot;">​</a></h2><ul><li><a href="https://docs.espressif.com/projects/arduino-esp32/en/latest/" target="_blank" rel="noreferrer">Documentation officielle ESP32</a></li><li><a href="https://platformio.org/" target="_blank" rel="noreferrer">Guide Platform IO</a></li><li><a href="https://www.amazon.fr/s?k=kit+esp32" target="_blank" rel="noreferrer">Kits de démarrage ESP32</a></li></ul><hr><h2 id="🌟-introduction-1" tabindex="-1">🌟 Introduction <a class="header-anchor" href="#🌟-introduction-1" aria-label="Permalink to &quot;🌟 Introduction&quot;">​</a></h2><p>L&#39;ESP32 est une carte de développement <strong>Wi-Fi et Bluetooth</strong> basée sur le module ESP32 d&#39;Espressif. Cette carte est parfaite pour les projets IoT grâce à ses fonctionnalités avancées et sa puissance. Voici ses caractéristiques principales :</p><ul><li><strong>Processeur Xtensa dual-core</strong> 32 bits cadencé à 240 MHz.</li><li><strong>Mémoire :</strong> 520 Ko de SRAM et 4 Mo de mémoire flash.</li><li><strong>Connectivité :</strong> Wi-Fi 802.11 b/g/n et Bluetooth 4.2 intégrés.</li><li><strong>Compatibilité :</strong> Programmable avec l&#39;IDE Arduino ou MicroPython.</li></ul><p>📌 <strong>Différence clé avec Arduino Uno :</strong> L&#39;ESP32 fonctionne uniquement en 3.3V, contrairement à l&#39;Arduino Uno (5V et 3.3V).</p><h2 id="✨-points-forts-de-l-esp32" tabindex="-1">✨ Points forts de l&#39;ESP32 <a class="header-anchor" href="#✨-points-forts-de-l-esp32" aria-label="Permalink to &quot;✨ Points forts de l&#39;ESP32&quot;">​</a></h2><ul><li>🔗 Connectivité intégrée Wi-Fi et Bluetooth.</li><li>🌍 Idéal pour les projets <strong>IoT</strong>.</li><li>🛠️ Compatible avec <strong>Arduino</strong> et <strong>MicroPython</strong>.</li><li>⚡ Puissance : Processeur à 240 MHz (contre 16 MHz pour l&#39;Arduino Uno).</li><li>🔋 Faible consommation d&#39;énergie, parfait pour les appareils autonomes.</li></ul><h2 id="📌-les-pins-de-l-esp32" tabindex="-1">📌 Les PINs de l&#39;ESP32 <a class="header-anchor" href="#📌-les-pins-de-l-esp32" aria-label="Permalink to &quot;📌 Les PINs de l&#39;ESP32&quot;">​</a></h2><h3 id="🖇️-principales-broches-disponibles" tabindex="-1">🖇️ Principales broches disponibles : <a class="header-anchor" href="#🖇️-principales-broches-disponibles" aria-label="Permalink to &quot;🖇️ Principales broches disponibles :&quot;">​</a></h3><ul><li><strong>Alimentation :</strong> Broches pour alimentation et masse.</li><li><strong>VBAT :</strong> Permet l&#39;alimentation via une batterie.</li><li><strong>VIN :</strong> Alimentation externe.</li><li><strong>GPIO :</strong> (General Purpose Input/Output) Broches d&#39;entrées/sorties numériques pour piloter des capteurs ou des actionneurs.</li><li><strong>ADC :</strong> Convertisseurs analogiques numériques pour lire des signaux analogiques.</li><li><strong>Communication série :</strong> UART, SPI, et I2C pour dialoguer avec d&#39;autres composants.</li></ul><img src="https://camo.githubusercontent." alt="Pin ESP32" style="width:500px;"><p><a href="https://github.com/thelastoutpostworkshop/gpio_viewer/blob/main/README.md" target="_blank" rel="noreferrer">Lien vers l&#39;ensemble des cartes ESP32 et leurs ports associés</a></p><h3 id="🔘-les-boutons-de-controle" tabindex="-1">🔘 Les boutons de contrôle : <a class="header-anchor" href="#🔘-les-boutons-de-controle" aria-label="Permalink to &quot;🔘 Les boutons de contrôle :&quot;">​</a></h3><ul><li><strong>RESET :</strong> Réinitialise le module.</li><li><strong>BOOT :</strong> Démarre le module ou met à jour son firmware.</li></ul><hr><h2 id="⚔️-esp32-vs-esp8266-quelle-carte-choisir" tabindex="-1">⚔️ ESP32 vs ESP8266 : Quelle carte choisir ? <a class="header-anchor" href="#⚔️-esp32-vs-esp8266-quelle-carte-choisir" aria-label="Permalink to &quot;⚔️ ESP32 vs ESP8266 : Quelle carte choisir ?&quot;">​</a></h2><table tabindex="0"><thead><tr><th><strong>Caractéristique</strong></th><th><strong>ESP32</strong></th><th><strong>ESP8266</strong></th></tr></thead><tbody><tr><td><strong>Processeur</strong></td><td>Dual-core à 240 MHz</td><td>Single-core à 80/160 MHz</td></tr><tr><td><strong>Mémoire</strong></td><td>520 Ko SRAM, 4 Mo flash</td><td>160 Ko SRAM, 4 Mo flash</td></tr><tr><td><strong>Wi-Fi + Bluetooth</strong></td><td>Oui (Bluetooth 4.2)</td><td>Wi-Fi uniquement</td></tr><tr><td><strong>Nombre de GPIO</strong></td><td>Plus de 30 broches</td><td>Moins de 20 broches</td></tr><tr><td><strong>Consommation d&#39;énergie</strong></td><td>Moyenne</td><td>Faible</td></tr><tr><td><strong>Prix</strong></td><td>Plus élevé</td><td>Plus économique</td></tr></tbody></table><blockquote><img src="https://i0.wp.com/randomnerdtutorials.com/wp-content/uploads/2019/08/esp32-vs-esp8266-devlopment-boards.jpg?w=750&amp;quality=100&amp;strip=all&amp;ssl=1" alt="ESP32 vs ESP8266" width="300"> ESP32 à gauche, ESP8266 à droite. </blockquote><p>Voici une section mise à jour avec les ressources utiles pour compléter votre exemple de cours. Ces liens fournissent des informations supplémentaires pour approfondir vos connaissances sur l&#39;ESP32 et les projets IoT.</p><hr><h2 id="🔗-ressources-utiles" tabindex="-1">🔗 Ressources utiles <a class="header-anchor" href="#🔗-ressources-utiles" aria-label="Permalink to &quot;🔗 Ressources utiles&quot;">​</a></h2><h3 id="📖-documentation-et-cours" tabindex="-1">📖 Documentation et cours <a class="header-anchor" href="#📖-documentation-et-cours" aria-label="Permalink to &quot;📖 Documentation et cours&quot;">​</a></h3><ul><li><a href="./../Arduino/arduino-code.html">Formation Arduino</a> :Apprenez à utiliser l&#39;Arduino IDE avec Arduino.</li><li><a href="https://docs.espressif.com/projects/arduino-esp32/en/latest/" target="_blank" rel="noreferrer">Documentation officielle ESP32</a></li></ul><h3 id="🎥-videos" tabindex="-1">🎥 Vidéos <a class="header-anchor" href="#🎥-videos" aria-label="Permalink to &quot;🎥 Vidéos&quot;">​</a></h3><ul><li><a href="https://www.youtube.com/watch?v=3GbyYQHQvV8" target="_blank" rel="noreferrer">Programmer des objets connectés en 15 min !! (Tuto ESPHome 2022)</a></li><li><a href="https://www.youtube.com/watch?v=RiYnucfy_rs" target="_blank" rel="noreferrer">Arduino To ESP32: How to Get Started!</a></li></ul><h3 id="🛠️-kits-et-exemples" tabindex="-1">🛠️ Kits et exemples <a class="header-anchor" href="#🛠️-kits-et-exemples" aria-label="Permalink to &quot;🛠️ Kits et exemples&quot;">​</a></h3><ul><li><a href="https://github.com/Freenove/Freenove_Ultimate_Starter_Kit_for_ESP32" target="_blank" rel="noreferrer">Freenove Ultimate Starter Kit for ESP32</a></li></ul>',42)]))}const g=t(a,[["render",n]]);export{h as __pageData,g as default};
